  var submenuElement = document.getElementById(&#39;submenu&#39;);
  var links = [
  { text: &#39;楷体&#39;, fontFamily: &#39;KaiTi&#39; }, // 添加 fontFamily 属性来指定字体
  { text: &#39;鹭霞文楷&#39;, fontFamily: &#39;LXGW WenKai Screen&#39; }, // 示例字体，你可以根据需要更改
  { text: &#39;Courier New&#39;, fontFamily: &#39;Courier New&#39; }, // 示例字体，你可以根据需要更改
  { text: &#39;原始字体&#39;, fontFamily: &#39;Noto Serif SC&#39; },
  { text: &#39;宋体&#39;, fontFamily: &#39;宋体&#39; }
];
// 创建一个函数来更改字体
function changeFont(fontFamily) {
  document.body.style.fontFamily = fontFamily;
    // 选择所有具有 .post-body 类的元素，并更改它们的字体家族
  var postBodies = document.getElementsByClassName(&#39;post-body&#39;);
  for (var i = 0; i &lt; postBodies.length; i++) {
    postBodies[i].style.fontFamily = fontFamily;
  }
}
 links.forEach(function(link) {
  var a = document.createElement(&#39;button&#39;);
  a.textContent = link.text;
  a.className = &#39;submenu-button&#39;; 
  a.onclick = function(event) {
  changeFont(link.fontFamily); // 调用 changeFont 函数，并传入字体名称
  };
  submenuElement.appendChild(a);
  });
  document.addEventListener(&#39;DOMContentLoaded&#39;, (event) =&gt; {
    const gearButton = document.getElementById(&#39;gear-button&#39;);
    const submenu = document.getElementById(&#39;submenu&#39;);
    gearButton.addEventListener(&#39;click&#39;, () =&gt; {
      submenu.classList.toggle(&#39;active&#39;);
    });
    // Optionally, close the submenu if a click is detected outside of it
    document.addEventListener(&#39;click&#39;, (event) =&gt; {
      if (!submenu.contains(event.target) &amp;&amp; event.target !== gearButton) {
        submenu.classList.remove(&#39;active&#39;);
      }
    });
  });
